Code tracing


What does each main function ouptut?
Assume all required libraries are included.
Refer to the next section for extra help.


// 1
int main() {
    int temp = 4;
    double temp2 = 2.5;
    float temp3 = 2.0;

    cout << temp3 << endl;
    cout << temp/ temp2 << endl;

    temp = temp2;
    cout << temp << endl;

    return 0;
}

// 2
int main() {
    int temp = 4;
    double temp2 = 2.5;
    double temp3 = 1.5;

    temp3 = temp / temp2;
    temp /= temp2;

    cout << temp3 << ", " << temp << endl;

    // some other primitive number variable types:
    float temp4 = 5.5;     // half the precision of double
    long temp5 = 9.1;
    short temp6 = 4.7;
    long long temp7 = 8.6;

    cout << temp4 + temp6 << endl;
    temp7 -= temp5;
    cout << temp7 << endl;

    return 0;
}

// 3
int main() {
    string jake = "jake";
    string james = "james";
    char a = 'a';

    jake = james;
    cout << jake << endl;
    jake = jake + james;
    cout << jake << endl;

    jake = "j";
    jake += a;
    a += 12;
    jake += a;
    a -= 8;
    jake += a;
    a = ((a/ 2) * 3) - (12*2 + 4); // hint: http://www.ascii-code.com/
    jake += a;
    cout << jake << endl;

    return 0;
}

// 4
int main() {
    int intgr = 20;
    char letter = 'a';
    double dbl = 10.67;

    cout << static_cast<char>(letter + static_cast<int>(dbl) * 2 - intgr * 2.5);
    cout << static_cast<char>((intgr * 2 + dbl * 4) + (dbl / 9));
    cout << " " << intgr % 11 + 1;
    string temp = "";
    temp += static_cast<char>(intgr + 12);
    temp += static_cast<char>(letter + static_cast<int>(dbl) - 1.67);
    temp += (18+ letter);
    cout << temp << " awesome!" << endl;

    return 0;
}




