// 1
int main() {
    char c1 = 'x';
    char c2 = c1 - 'a' + 'A';

    cout << c1 << ' ' << c2 << endl;

    c1 += 1;
    c2 += 1;

    cout << c1 << ' ' << c2 << endl;

    return 0;
}




// 2
int main() {
    int i = 2;
    int j = 5;
    int k = -3;

    if (i > k) {
        i *= k;
    }

    if (j < i) {
        j *= i;
    } else {
        j /= 5;
    }

    if (j * 3 > -k) {
        k = -k / 3;
    } else if (j * 3 < -k) {
        j = j * 3;
        k = -k;
    }

    if (i > 0) {
        i = k;
    } else if (k > 0) {
        k = i;
    } else if (j > 0) {
        j = i;
    }

    cout << "i = " << i << endl;
    cout << "j = " << j << endl;
    cout << "k = " << k << endl;

    return 0;
}




// 3
int main() {
    int i = 2;
    int j = 5;
    int k = -3;
    bool b1 = true;

    if (i > k && !b1) {
        i *= k;
    }

    if (j < i && b1) {
        j *= i;
    } else {
        j /= 5;
    }

    if (!(j * 3 > -k)) {
        k = -k / 3;
    } else if (j * 3 <= -k) {
        j = j * 3;
        k = -k;
    }

    if (!(i > 0) || (j - i > 0)) {
        i = k;
    } else if (((k > 0) && !(-k < 0)) || b1) {
        k = i;
    } else if (!(j > 0) || (i - j > 0)) {
        j = i;
    }

    cout << "i = " << i << endl;
    cout << "j = " << j << endl;
    cout << "k = " << k << endl;

    return 0;
}




// 4
int main() {
    int i = 2;
    int j = 5;
    int k = -3;
    bool b1 = true;

    if (i > k && b1) {
        if (-i < -k || !b1) {
            k += 5;
        }
        i *= k;
    }

    if (j >= i) {
        if (j == i) {
            j -= 6;
        }
        j /= i;
    } else {
        if (j > k && j < i) {
            j -= 1;
        }
        j *= 5;
    }

    if (j * 3 > k) {
        if (!(j * 3 <= -k)) {
            b1 = !b1;
        }
        k = -k * 3;
    } else if (j * 3 <= -k || b1) {
        j = j * 3;
        k = -k;
    }

    if (!(i > 0) || (j - i > 0)) {
        i = k;
        if (i == k) {
            k += 6;
        }
    } else if (((k > 0) && !(-k < 0)) || b1) {
        k = i;
        if (b1) {
            i += 6;   
        }
    } else if (!(j > 0) || (i - j > 0)) {
        j = i;
        if (b1) {
            j += 6;
        } else if (b1 || !b1) {
            i += 3;
        }
    }

    cout << "i = " << i << endl;
    cout << "j = " << j << endl;
    cout << "k = " << k << endl;

    return 0;
}

//5
int main(){
    int j = 1;
    int k = 2;
    bool b = false;

    if(j || k){
        k = 0;
    }

    if(k){
        j = 0;
    }

    if((j == k) || (b = true)){
        k -= 2;
    }
    else if(!b){
        j += 3;
        k -= 4;
    }
    else{
        j *= k;
    }


    cout << "j = " << j << endl;
    cout << "k = " << k << endl;
    return 0;
}
